    //Método conectaPostgre()
    public static Statement conectaPostgre(){
        try {
            Class.forName("org.postgresql.Driver");
            
            Connection conexao = DriverManager.getConnection("jdbc:postgresql://localhost:5432/FaculdadeDB","postgres","");
            if(conexao != null) {
               System.out.println("Banco de dados conectado com sucesso!");
               Statement stm = conexao.createStatement();
               return stm;
            }
            else {
               System.out.println("Conexão falhou!"); 
            }
        } catch (ClassNotFoundException ex) {
            //Logger.getLogger(JaDBexemplo.class.getName()).log(Level.SEVERE, null, ex);
            System.out.println("Driver nao localizado.");
        } catch (SQLException ex) {
            //Logger.getLogger(JaDBexemplo.class.getName()).log(Level.SEVERE, null, ex);
            System.out.println("Conexão com DB falhou.");
        }
        return null;
    }
    
    //Método consultaTabela(...)
    public ResultSet consultaTabela(Statement stm, String campos, String tabela ){
        String sql = "select "+ campos +" from "+tabela;
        try {
            ResultSet resultado = stm.executeQuery(sql);
            System.out.println("Query realizada!");
            /*
            while(resultado.next()){
                System.out.println("Campo1:"+resultado.getString("campo1")
                        +"Campo2:"+resultado.getString("Campo2"));
            }
            */
            return resultado;
        } catch (SQLException ex) {
            //Logger.getLogger(JaDBexemplo.class.getName()).log(Level.SEVERE, null, ex);
            System.out.println("Falha ao execultar a QUERY!");
            JOptionPane.showMessageDialog(rootPane, "Falha ao executar a QUERY!");
            
        }
        return null;
    }
    
    //Método insereRegistro(...)
    public void insereRegistro(Statement stm, String tabela, String campos){
        
        String sql = "INSERT INTO ";
        switch(tabela) {
            case "alunos" -> sql += "alunos (cpf,nome,email,fone,nascimento)"
                        +" values " 
                        +campos;//" ('cpf1', 'nome1', 'email1', 'fone1','2025-11-11');";
            case "cursos" -> sql += "cursos (nome,cargaHr)"
                        +" values " 
                        +campos;//" ('nome3', 60);";
            case "professores" -> sql += "professores (nome,email,salario)"
                        +" values " 
                        +campos;//" ('nome2', 'email2', 15000.2);";
            case "cursosRequisitos" -> sql += "cursosRequisitos (cursoID,cursoReqID)"
                        +" values " 
                        +campos;//" (8,7);";
            case "turmas" -> sql += "turmas (cursoID,profID)"
                        +" values " 
                        +campos;//" (9,7);";
            case "matriculas" -> sql += "matriculas (status,turmaID,alunoID)"
                        +" values " 
                        +campos;//" ('matriculado/reprovado',1,7);";
            default -> {
            }
        }
        // code block
        System.out.println(sql);
        try {
            stm.executeUpdate(sql);
            System.out.println("Novo registro inserido!");
            
        } catch (SQLException ex) {
            //Logger.getLogger(JaDBexemplo.class.getName()).log(Level.SEVERE, null, ex);
            System.out.println("Falha ao executar'"+sql+"!");
            JOptionPane.showMessageDialog(rootPane, "Falha ao executar o INSERT sql!");
        }
    }
    
    //Método atualizaRegistro(...)
    public void atualizaRegistro(Statement stm, String tabela, String campos, String ID){
        //UPDATE titulos SET titulo = 'newTitulo' WHERE isbn = 1;
        
        
        String sql = "UPDATE ";
        switch(tabela) {
            case "alunos" -> sql += "alunos SET "+campos //" cpf='newCpf',nome='newNome',email='newEmail',fone='newFone',nascimento='2025-1-2' ";
                    +" WHERE alunoID = " +ID;
            case "cursos" -> sql += "cursos SET "+campos //" nome= 'newNome, cargaHr= 30 ";
                    +" WHERE cursoID = " +ID;
            case "professores" -> sql += "professores SET "+campos //" nome='newNome',email='newEmail',salario= 15000.2 ";
                    +" WHERE profID = " +ID;
            case "cursosRequisitos" -> sql += "cursosRequisitos SET "+campos //" cursoReqID = 2 ";
                    +" WHERE cursoID = " +ID;
            case "turmas" -> sql += "turmas SET "+campos //" cursoID = 2, profID = 2 ";
                    +" WHERE turmaID = " +ID;
            case "matriculas" -> sql += "matriculas SET "+campos //" status='newStatus',turmaID=5,alunoID=11 ";
                    +" WHERE matriculaID = " +ID;
            default -> {
            }
        }
        System.out.println(sql);
        try {
            stm.executeUpdate(sql);
            System.out.println("Comando UPDATE executado!");
            
        } catch (SQLException ex) {
            //Logger.getLogger(JaDBexemplo.class.getName()).log(Level.SEVERE, null, ex);
            System.out.println("Falha ao executar o UPDATE sql!");
            JOptionPane.showMessageDialog(rootPane, "Falha ao atualizar '"+ID+"'!");
            
        }
    }
    
    
