    private void buttonDeletarCursosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonDeletarCursosActionPerformed
        // TODO add your handling code here:

        //--> checar se o campo ID não está vazio
        if(fieldCcursoID.getText().isEmpty())
        {
            JOptionPane.showMessageDialog(rootPane, "Informe o ID!");
            return;
        }
        
        //Confirmação
        if(JOptionPane.showConfirmDialog(rootPane, "Deseja excluir este registro?")!=0)return;

        //--> remover o registro

        // Conexão
        Statement stm = conectaPostgre();
        if(stm == null){return;}
        // Remove
        String id = fieldCcursoID.getText();
        String sql = "delete from cursos where cursoID = " + id;
        try {
            stm.executeUpdate(sql);
        } catch (SQLException ex) {
            //Logger.getLogger(JFrameGUI.class.getName()).log(Level.SEVERE, null, ex);
            System.out.println("Falha ao deletar '"+id+"'!");
            JOptionPane.showMessageDialog(rootPane, "Falha ao deletar '"+id+"'!");
        }

        //Mostrar registros
        buttonExibirCursosActionPerformed(null);

    }//GEN-LAST:event_buttonDeletarCursosActionPerformed

    private void buttonAtualizarCursosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonAtualizarCursosActionPerformed
        // TODO add your handling code here:

        //-->necessário "ID"
        //--> checar se o campo ID não está vazio
        if(fieldCcursoID.getText().isEmpty()
                ||(fieldCnome.getText().isEmpty() && fieldCcargaHr.getText().isEmpty()))
        {
            JOptionPane.showMessageDialog(rootPane, "Informe o ID e os valores que deseja atualizar!");
            return;
        }

        // Conexão
        Statement stm = conectaPostgre();
        if(stm == null){return;}

        //-->organiza strings e chama atualizaRegistro(...)
        
        String campos = "";
        //Não atualiza campos que estiverem vazios
        String setNome = (fieldCnome.getText().isEmpty())?"":"nome = '"+fieldCnome.getText()+"' ";
        String setCargaHr= (fieldCcargaHr.getText().isEmpty())?"":"cargaHr = "+fieldCcargaHr.getText()+" ";
        // colocar as virgulas
        String[] camposList = {setNome,setCargaHr};
        boolean first = true;
        for(String item : camposList) {
            if(item.isEmpty())continue;
            if(first) {
                campos+=item;
                first=false;
            } else {
                campos+=","+item;
            }
        }
        atualizaRegistro(stm,"cursos",campos,fieldCcursoID.getText());

        //mostra atualizado
        buttonExibirCursosActionPerformed(null);
    }//GEN-LAST:event_buttonAtualizarCursosActionPerformed

    private void buttonExibirCursosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonExibirCursosActionPerformed
        try {
            // TODO add your handling code here:
            Statement stm = conectaPostgre();
            if(stm == null){return;}
            
            
            ResultSet cursosRequisitos = consultaTabela(stm,"*","cursosRequisitos");
            while(tableModelCursosReq.getRowCount()!=0){ //jTable -->addRow
                tableModelCursosReq.removeRow(0);
            }
            while(cursosRequisitos.next()){
               //jTable --> tableModel.addRow
                String[] rowData = {
                    cursosRequisitos.getString("cursoid"),
                    cursosRequisitos.getString("cursoreqid")
                };
                tableModelCursosReq.addRow(rowData);
            }
            
            ResultSet resultadoConsulta = consultaTabela(stm,"*","cursos");
            
            while(tableModelCursos.getRowCount()!=0){ //jTable -->addRow
                tableModelCursos.removeRow(0);
            }
            
            String req = "";
            Boolean first = true;
            int i;
            while(resultadoConsulta.next()){
                i=0;
                while(i<tableModelCursosReq.getRowCount()){
                    System.out.println(tableModelCursosReq.getValueAt(i, 0)+resultadoConsulta.getString("cursoid"));
                    if(Integer.parseInt(tableModelCursosReq.getValueAt(i, 0).toString())==Integer.parseInt(resultadoConsulta.getString("cursoid"))){
                        req += (first)?tableModelCursosReq.getValueAt(i, 1).toString():", "+tableModelCursosReq.getValueAt(i, 1).toString();
                        first = false;
                    }
                    i++;
                }
                System.out.println(req);
                //jTable --> tableModel.addRow
                String[] rowData = {
                    resultadoConsulta.getString("cursoid"),
                    resultadoConsulta.getString("nome"),
                    resultadoConsulta.getString("cargahr"),
                    req
                };
                tableModelCursos.addRow(rowData);
                //tableModel.addRow(rowData1); //vector<>
                
                
                req = "";
                first = true;
              
            }

            //jTable --> set
            jTableCursos.setModel(tableModelCursos);

        } catch (SQLException ex) {
            Logger.getLogger(jFrameTela1.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_buttonExibirCursosActionPerformed

