        //--> checar se nenhum os campos está vazio
        if(fieldMStatus.getText().isEmpty()
            ||choiceMturmaID.getSelectedItem().isEmpty()
            ||choiceMalunoID.getSelectedItem().isEmpty())                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
        {
            JOptionPane.showMessageDialog(rootPane, "Preencha todos os campos necessários!\n(ID será gerado pelo sistema)");
            return;
        }
        
        // Verifica pré-requisitos e emite alerta
        //...
        Statement stm = conectaPostgre();
        if(stm == null){return;}
        ResultSet matriculas_do_aluno_nos_prerequisitos = null;
        String sql = "SELECT m.*" +
            " FROM matriculas m" +
            " INNER JOIN turmas t ON m.turmaID = t.turmaID" +
            " WHERE m.alunoID = " +choiceMalunoID.getSelectedItem()+
            " AND t.cursoID IN (" +
            "    SELECT cursoReqID " +
            "    FROM cursosRequisitos" +
            "    WHERE cursoID = (" +
            "        SELECT cursoID " +
            "        FROM turmas " +
            "        WHERE turmaID = " +choiceMturmaID.getSelectedItem()+
            "    )" +
            ");";
        int cont_matriculas = 0, cont_req = 0;
        try {
            matriculas_do_aluno_nos_prerequisitos = stm.executeQuery(sql);
            System.out.println("Query realizada!");
            /*
            while(resultado.next()){
                System.out.println("Campo1:"+resultado.getString("campo1")
                        +"Campo2:"+resultado.getString("Campo2"));
            }
            */
            while(matriculas_do_aluno_nos_prerequisitos.next()){
                cont_matriculas++;
            }
        } catch (SQLException ex) {
            //Logger.getLogger(JaDBexemplo.class.getName()).log(Level.SEVERE, null, ex);
            System.out.println("Falha ao execultar a QUERY!");
            JOptionPane.showMessageDialog(rootPane, "Falha ao executar a QUERY!");
        }
        
        
        
        ResultSet cursosRequisitos = consultaTabela(stm,"*","cursos " +
            " INNER JOIN cursosRequisitos " +
            "    ON cursos.cursoID = cursosRequisitos.cursoID " +
            "WHERE cursos.cursoID = ("+
            "    SELECT cursoID" +
            "    FROM turmas" +
            "    WHERE turmaID = " +choiceMturmaID.getSelectedItem()+
            ")");
        try {
            while(cursosRequisitos.next()){
                //jTable --> tableModel.addRow
                cont_req++;
            }
        } catch (SQLException ex) {
            Logger.getLogger(jFrameTela1.class.getName()).log(Level.SEVERE, null, ex);
        }
        if(cont_matriculas < cont_req)                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
        {
            JOptionPane.showMessageDialog(rootPane, "Este aluno não possui matricula em todos os requisitos!");
        }
        
        //--> inserir novo registro na tabela

        // Conexão
        stm = conectaPostgre();
        if(stm == null){return;}

        //" ('matriculado/reprovado',1,7);";;
        //organiza string e chama insereRegistro(...)
        String campos  = "('"+fieldMStatus.getText()+"', "+ choiceMturmaID.getSelectedItem()+", "+ choiceMalunoID.getSelectedItem()+");";
        insereRegistro(stm,"matriculas",campos);

        //Mostrar Inserido
        if(!fieldMmatriculaID.getText().isEmpty()){
            JOptionPane.showMessageDialog(rootPane, "O ID será gerado pelo sistema.");
            fieldMmatriculaID.setText("");
        }
        buttonExibirMatriculaActionPerformed(null); //botão exibir
