    private void jButtonAddReqActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAddReqActionPerformed
        // TODO add your handling code here:

        if(fieldCcursoID.getText().isEmpty()){
            JOptionPane.showMessageDialog(rootPane, "Informe o CursoID!");
            return;
        }
        
        
        List<String> listReq = jListCreq.getSelectedValuesList();

        String campos;

        for ( int i = 0; i < listReq.size(); i++ ) {
            try{
            Statement stm = conectaPostgre();
            if(stm == null){return;}
            campos  = "("+fieldCcursoID.getText()+", "+ listReq.get(i)+");";
            insereRegistro(stm,"cursosRequisitos", campos);
            }
            catch(Exception ex){
            }
        }
        buttonExibirCursosActionPerformed(evt);
        
        
        
    }//GEN-LAST:event_jButtonAddReqActionPerformed

    private void jButtonRemReqActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonRemReqActionPerformed
        // TODO add your handling code here:
        
        //--> checar se o campo ID não está vazio
        if(fieldCcursoID.getText().isEmpty()){
            JOptionPane.showMessageDialog(rootPane, "Informe o CursoID!");
            return;
        }
        //Confirmação
        if(JOptionPane.showConfirmDialog(rootPane, "Deseja remover este requisitos?")!=0)return;

        //--> remover o requisitos
        List<String> listReq = jListCreq.getSelectedValuesList();

        for ( int i = 0; i < listReq.size(); i++ ) {
            
            Statement stm = conectaPostgre();
            if(stm == null){return;}
            String sql = "delete from cursosRequisitos where cursoID = " + fieldCcursoID.getText() + " and cursoReqID = "+listReq.get(i);
            try {
                stm.executeUpdate(sql);
            } catch (SQLException ex) {
                //Logger.getLogger(JFrameGUI.class.getName()).log(Level.SEVERE, null, ex);
                System.out.println("Falha ao executar 'delete from cursosRequisitos where cursoID = " + fieldCcursoID.getText() + " and cursoReqID = "+listReq.get(i)+"'!");
                JOptionPane.showMessageDialog(rootPane, "Falha ao remover '"+listReq.get(i)+"'!");
            }   
        }
        buttonExibirCursosActionPerformed(null);
    }//GEN-LAST:event_jButtonRemReqActionPerformed

    private void jButtonAlterarReqActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAlterarReqActionPerformed
        // TODO add your handling code here:
        if(fieldCcursoID.getText().isEmpty()){
            JOptionPane.showMessageDialog(rootPane, "Informe o CursoID!");
            return;
        }
        
        switch(jButtonAlterarReq.getText()){
            case "Alterar":
                //Habilita botões
                jListCreq.setVisible(true);
                jButtonAddReq.setVisible(true);
                jButtonRemReq.setVisible(true);
                jButtonAlterarReq.setText("Cancelar");
                try {
                    Statement stm = conectaPostgre();
                    if(stm == null){return;}
                    ResultSet resultadoConsulta =consultaTabela(stm,"*","cursos");

                    while(resultadoConsulta.next()){
                        //add CursoID
                        listModelCreq.addElement(resultadoConsulta.getString("cursoid"));
                }
                //jListCreq.setModel(listModelCreq);
                } catch (SQLException ex) {
                    //Logger.getLogger(jFrameTela1.class.getName()).log(Level.SEVERE, null, ex);
                    System.out.println("Falha ao executar a consulta!");
                }
                break;
            case "Cancelar":
                //Desabilita botões
                jListCreq.setVisible(false);
                jButtonAddReq.setVisible(false);
                jButtonRemReq.setVisible(false);
                jButtonAlterarReq.setText("Alterar");
                break;
            default:
        }
        
        
    }//GEN-LAST:event_jButtonAlterarReqActionPerformed

